  --sidebar-foreground: hsl(120, 25%, 95%);
  --sidebar-primary: hsl(142, 76%, 45%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(210, 20%, 12%);
  --sidebar-accent-foreground: hsl(142, 76%, 45%);
  --sidebar-border: hsl(210, 20%, 15%);
  --sidebar-ring: hsl(142, 76%, 45%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom styles for financial dashboard */
.expense-card {
  @apply bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-shadow;
}

.category-badge {
  @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium;
}

.stat-card {
  @apply bg-white overflow-hidden shadow rounded-lg;
}

.insight-card {
  @apply flex items-start space-x-3 p-4 rounded-lg border;
}

/* Chart container styles */
.chart-container {
  @apply h-64 w-full;
}

/* Animation for loading states */
.loading-skeleton {
  @apply animate-pulse bg-gray-200 rounded;
}

/* Custom scrollbar */
.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: hsl(var(--muted));
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: hsl(var(--border));
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground));
}

