"use client";
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Info, CheckCircle, AlertTriangle, Lightbulb, RefreshCw } from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

const getInsightIcon = (type: string) => {
  switch (type) {
    case "alert":
      return <Info className="w-5 h-5 text-blue-600" />;
    case "goal":
      return <CheckCircle className="w-5 h-5 text-green-600" />;
    case "warning":
      return <AlertTriangle className="w-5 h-5 text-amber-600" />;
    case "recommendation":
      return <Lightbulb className="w-5 h-5 text-purple-600" />;
    default:
      return <Info className="w-5 h-5 text-blue-600" />;
  }
};

const getInsightColor = (type: string, priority: string) => {
  if (priority === "high") {
    switch (type) {
      case "alert":
        return "bg-red-50 border-red-300 text-red-900";
      case "warning":
        return "bg-orange-50 border-orange-300 text-orange-900";
      default:
        return "bg-red-50 border-red-300 text-red-900";
    }
  }

  switch (type) {
    case "alert":
      return "bg-red-50 border-red-200 text-red-800";
    case "goal":
      return "bg-green-50 border-green-200 text-green-800";
    case "warning":
      return "bg-amber-50 border-amber-200 text-amber-800";
    case "recommendation":
      return "bg-blue-50 border-blue-200 text-blue-800";
    default:
      return "bg-gray-50 border-gray-200 text-gray-800";
  }
};

export function AIInsights() {
  const { toast } = useToast();
  const [customInsights, setCustomInsights] = useState<any>(null);

  const { data: insights, isLoading } = useQuery({
    queryKey: ["/api/insights"],
    retry: false,
  });

  const generateInsightsMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/insights/generate");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/insights"] });
      toast({
        title: "New Insights Generated",
        description:
          "AI has analyzed your spending and generated new recommendations.",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to generate new insights. Please try again.",
        variant: "destructive",
      });
    },
  });

  const markInsightReadMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("PUT", `/api/insights/${id}/read`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/insights"] });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
    },
  });

  const handleGenerateInsights = () => {
    generateInsightsMutation.mutate();
  };

  const handleMarkAsRead = (id: string) => {
    markInsightReadMutation.mutate(id);
  };

  // üîπ New: Fetch custom range insights
  const handleCustomInsights = async () => {
    try {
      const res = await apiRequest("POST", "/api/insights/custom", {
        start: "2025-08-01",
        end: "2025-08-31",
      });
      setCustomInsights(res);
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to fetch custom insights.",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <Card className="bg-white shadow animate-pulse">
        <CardHeader>
          <div className="h-6 bg-gray-200 rounded w-1/3"></div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-20 bg-gray-200 rounded"></div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-white shadow" data-testid="card-ai-insights">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center space-x-2">
            <Lightbulb className="h-5 w-5 text-amber-500" />
            <span>AI Insights</span>
          </CardTitle>
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleGenerateInsights}
              disabled={generateInsightsMutation.isPending}
              data-testid="button-generate-insights"
            >
              <RefreshCw
                className={`h-4 w-4 mr-2 ${
                  generateInsightsMutation.isPending ? "animate-spin" : ""
                }`}
              />
              {generateInsightsMutation.isPending ? "Generating..." : "Generate"}
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={handleCustomInsights}
              data-testid="button-custom-insights"
            >
              Custom Range
            </Button>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {/* üîπ Default saved insights */}
        {(insights as any) && (insights as any).length > 0 ? (
          <div className="space-y-4" data-testid="insights-list">
            {(insights as any)
              .sort((a: any, b: any) => {
                const priorityOrder = { high: 3, medium: 2, low: 1 };
                return (
                  (priorityOrder[b.priority as keyof typeof priorityOrder] || 0) -
                  (priorityOrder[a.priority as keyof typeof priorityOrder] || 0)
                );
              })
              .map((insight: any) => (
                <div
                  key={insight.id}
                  className={`p-4 rounded-lg border ${getInsightColor(
                    insight.type,
                    insight.priority
                  )} transition-all hover:shadow-md`}
                  data-testid={`insight-${insight.id}`}
                >
                  <div className="flex items-start space-x-3">
                    <div className="flex-shrink-0 mt-0.5">
                      {getInsightIcon(insight.type)}
                    </div>
                    <div className="flex-1 min-w-0">
                      <h4
                        className="text-sm font-medium mb-1"
                        data-testid={`insight-title-${insight.id}`}
                      >
                        {insight.title}
                      </h4>
                      <p
                        className="text-sm opacity-90 leading-relaxed"
                        data-testid={`insight-message-${insight.id}`}
                      >
                        {insight.description || insight.message}
                      </p>
                      <div className="flex items-center justify-between mt-3">
                        <span className="text-xs text-gray-600">
                          {insight.priority === "high" && (
                            <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                              High Priority
                            </span>
                          )}
                          {insight.priority === "medium" && (
                            <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                              Medium Priority
                            </span>
                          )}
                          {insight.priority === "low" && (
                            <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                              Low Priority
                            </span>
                          )}
                        </span>
                        {insight.isRead === "false" && (
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => handleMarkAsRead(insight.id)}
                            className="text-xs"
                            data-testid={`button-mark-read-${insight.id}`}
                          >
                            Mark as Read
                          </Button>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        ) : (
          <div className="text-center py-8">
            <Lightbulb className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">
              No insights yet
            </h3>
            <p className="mt-1 text-sm text-gray-500">
              Add some expenses to get AI-powered insights and recommendations.
            </p>
            <div className="mt-6">
              <Button
                onClick={handleGenerateInsights}
                disabled={generateInsightsMutation.isPending}
                data-testid="button-generate-first-insights"
              >
                <RefreshCw
                  className={`h-4 w-4 mr-2 ${
                    generateInsightsMutation.isPending ? "animate-spin" : ""
                  }`}
                />
                Generate First Insights
              </Button>
            </div>
          </div>
        )}

        {/* üîπ Custom insights result */}
        {customInsights && (
          <div className="mt-6 border-t pt-4">
            <h3 className="font-bold text-lg">Custom Insights (Aug 2025)</h3>
            <p className="mt-2 text-sm text-gray-700">
              <strong>Recommendations:</strong> {customInsights.recommendations}
            </p>
            <p className="mt-2 text-sm text-gray-700">
              <strong>Savings Tips:</strong> {customInsights.savingsTips}
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/budget-settings.ts.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { insertBudgetSchema, type InsertBudget } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Plus, Edit, Trash2, Target, Calendar } from "lucide-react";

interface BudgetSettingsProps {
  open: boolean;
  onClose: () => void;
}

export function BudgetSettings({ open, onClose }: BudgetSettingsProps) {
  const { toast } = useToast();
  const [showForm, setShowForm] = useState(false);
  const [editingBudget, setEditingBudget] = useState<any>(null);

  const form = useForm<InsertBudget>({
    resolver: zodResolver(insertBudgetSchema),
    defaultValues: {
      categoryId: "",
      amount: "",
      period: "monthly",
      startDate: new Date().toISOString().split('T')[0],
      endDate: new Date(new Date().setMonth(new Date().getMonth() + 1)).toISOString().split('T')[0],
    },
  });

  const { data: budgets, isLoading } = useQuery({
    queryKey: ['/api/budgets'],
    retry: false,
    enabled: open,
  });

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
    enabled: open,
  });

  const createBudgetMutation = useMutation({
    mutationFn: async (data: InsertBudget) => {
      return await apiRequest("POST", "/api/budgets", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/budgets'] });
      queryClient.invalidateQueries({ queryKey: ['/api/analytics/stats'] });
      toast({
        title: "Budget Created",
        description: "Your budget has been successfully set.",
      });
      form.reset();
      setShowForm(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create budget. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteBudgetMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/budgets/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/budgets'] });
      toast({
        title: "Budget Deleted",
        description: "Budget has been removed successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to delete budget. Please try again.",
        variant: "destructive",
      });
    },
  });

  const formatCurrency = (amount: string) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
    }).format(parseFloat(amount));
  };

  const getCategoryName = (categoryId: string) => {
    const category = (categories as any)?.find((cat: any) => cat.id === categoryId);
    return category?.name || 'All Categories';
  };

  const onSubmit = (data: InsertBudget) => {
    createBudgetMutation.mutate(data);
  };

  const handleEdit = (budget: any) => {
    setEditingBudget(budget);
    form.reset({
      categoryId: budget.categoryId || "",
      amount: budget.amount,
      period: budget.period,
      startDate: budget.startDate,
      endDate: budget.endDate,
    });
    setShowForm(true);
  };

  const handleDelete = (id: string) => {
    if (confirm('Are you sure you want to delete this budget?')) {
      deleteBudgetMutation.mutate(id);
    }
  };

  const resetForm = () => {
    setEditingBudget(null);
    form.reset();
    setShowForm(false);
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-4xl max-h-[90vh] overflow-y-auto" data-testid="modal-budget-settings">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Target className="h-5 w-5" />
            <span>Budget Settings</span>
          </DialogTitle>
          <DialogDescription>
            Set spending limits for different categories to track your financial goals.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Add Budget Button */}
          <div className="flex justify-between items-center">
            <h3 className="text-lg font-medium">Your Budgets</h3>
            <Button 
              onClick={() => setShowForm(true)}
              data-testid="button-add-budget"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Budget
            </Button>
          </div>

          {/* Budget List */}
          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {[...Array(4)].map((_, i) => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-4">
                    <div className="h-20 bg-gray-200 rounded"></div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4" data-testid="budgets-list">
              {(budgets as any) && (budgets as any).length > 0 ? (
                (budgets as any).map((budget: any) => (
                  <Card key={budget.id} className="relative">
                    <CardHeader className="pb-3">
                      <div className="flex justify-between items-start">
                        <div>
                          <CardTitle className="text-base" data-testid={`budget-category-${budget.id}`}>
                            {getCategoryName(budget.categoryId)}
                          </CardTitle>
                          <div className="flex items-center space-x-2 mt-1">
                            <Badge variant="outline" className="text-xs">
                              <Calendar className="h-3 w-3 mr-1" />
                              {budget.period}
                            </Badge>
                          </div>
                        </div>
                        <div className="flex space-x-1">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleEdit(budget)}
                            data-testid={`button-edit-budget-${budget.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleDelete(budget.id)}
                            data-testid={`button-delete-budget-${budget.id}`}
                          >
                            <Trash2 className="h-4 w-4 text-red-500" />
                          </Button>
                        </div>
                      </div>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-2">
                        <div className="flex justify-between items-center">
                          <span className="text-sm text-gray-600">Budget</span>
                          <span className="font-medium" data-testid={`budget-amount-${budget.id}`}>
                            {formatCurrency(budget.amount)}
                          </span>
                        </div>
                        <div className="text-xs text-gray-500">
                          {budget.startDate} to {budget.endDate}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))
              ) : (
                <div className="col-span-2 text-center py-8">
                  <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No budgets set</h3>
                  <p className="text-gray-600 mb-4">
                    Start by creating your first budget to track spending.
                  </p>
                  <Button onClick={() => setShowForm(true)}>
                    <Plus className="h-4 w-4 mr-2" />
                    Create First Budget
                  </Button>
                </div>
              )}
            </div>
          )}

          {/* Budget Form */}
          {showForm && (
            <Card className="border-2 border-primary/20">
              <CardHeader>
                <CardTitle className="text-lg">
                  {editingBudget ? 'Edit Budget' : 'Create New Budget'}
                </CardTitle>
              </CardHeader>
              <CardContent>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {/* Category */}
                    <div>
                      <Label htmlFor="category">Category</Label>
                      <Select 
                        value={form.watch('categoryId') || ''} 
                        onValueChange={(value) => form.setValue('categoryId', value)}
                      >
                        <SelectTrigger data-testid="select-budget-category">
                          <SelectValue placeholder="Select category (optional)" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="all">All Categories</SelectItem>
                          {(categories as any)?.map((category: any) => (
                            <SelectItem key={category.id} value={category.id}>
                              <div className="flex items-center space-x-2">
                                <span className="text-sm">{category.icon}</span>
                                <span>{category.name}</span>
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    {/* Amount */}
                    <div>
                      <Label htmlFor="amount">Budget Amount *</Label>
                      <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                          <span className="text-gray-500 text-sm">‚Çπ</span>
                        </div>
                        <Input
                          id="amount"
                          type="number"
                          step="0.01"
                          placeholder="5000.00"
                          className="pl-7"
                          {...form.register('amount')}
                          data-testid="input-budget-amount"
                        />
                      </div>
                      {form.formState.errors.amount && (
                        <p className="text-red-500 text-xs mt-1">{form.formState.errors.amount.message}</p>
                      )}
                    </div>

                    {/* Period */}
                    <div>
                      <Label htmlFor="period">Period *</Label>
                      <Select 
                        value={form.watch('period')} 
                        onValueChange={(value) => form.setValue('period', value as any)}
                      >
                        <SelectTrigger data-testid="select-budget-period">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="weekly">Weekly</SelectItem>
                          <SelectItem value="monthly">Monthly</SelectItem>
                          <SelectItem value="yearly">Yearly</SelectItem>
                        </SelectContent>
                      </Select>
                      {form.formState.errors.period && (
                        <p className="text-red-500 text-xs mt-1">{form.formState.errors.period.message}</p>
                      )}
                    </div>

                    {/* Start Date */}
                    <div>
                      <Label htmlFor="startDate">Start Date *</Label>
                      <Input
                        id="startDate"
                        type="date"
                        {...form.register('startDate')}
                        data-testid="input-budget-start-date"
                      />
                      {form.formState.errors.startDate && (
                        <p className="text-red-500 text-xs mt-1">{form.formState.errors.startDate.message}</p>
                      )}
                    </div>

                    {/* End Date */}
                    <div>
                      <Label htmlFor="endDate">End Date *</Label>
                      <Input
                        id="endDate"
                        type="date"
                        {...form.register('endDate')}
                        data-testid="input-budget-end-date"
                      />
                      {form.formState.errors.endDate && (
                        <p className="text-red-500 text-xs mt-1">{form.formState.errors.endDate.message}</p>
                      )}
                    </div>
                  </div>

                  {/* Form Buttons */}
                  <div className="flex space-x-2 pt-4">
                    <Button 
                      type="submit" 
                      disabled={createBudgetMutation.isPending}
                      data-testid="button-submit-budget"
                    >
                      {editingBudget ? 'Update Budget' : 'Create Budget'}
                    </Button>
                    <Button 
                      type="button" 
                      variant="outline" 
                      onClick={resetForm}
                      data-testid="button-cancel-budget"
                    >
                      Cancel
                    </Button>
                  </div>
                </form>
              </CardContent>
            </Card>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

