import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { insertCategorySchema, type InsertCategory } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Plus, Edit, Trash2, Tag, Palette } from "lucide-react";

interface CategoriesManagerProps {
  open: boolean;
  onClose: () => void;
}

const PRESET_COLORS = [
  "#ef4444", "#f97316", "#f59e0b", "#eab308", 
  "#84cc16", "#22c55e", "#10b981", "#14b8a6",
  "#06b6d4", "#3b82f6", "#6366f1", "#8b5cf6",
  "#a855f7", "#d946ef", "#ec4899", "#f43f5e"
];

const PRESET_ICONS = [
  "üçΩÔ∏è", "üöó", "üõçÔ∏è", "üé¨", "üì±", "üè•", "üìö", "‚úàÔ∏è",
  "üí∞", "üè†", "‚ö°", "üéÆ", "üëï", "‚òï", "üéµ", "üíä"
];

export function CategoriesManager({ open, onClose }: CategoriesManagerProps) {
  const { toast } = useToast();
  const [showForm, setShowForm] = useState(false);
  const [editingCategory, setEditingCategory] = useState<any>(null);

  const form = useForm<InsertCategory>({
    resolver: zodResolver(insertCategorySchema),
    defaultValues: {
      name: "",
      color: PRESET_COLORS[0],
      icon: PRESET_ICONS[0],
    },
  });

  const { data: categories, isLoading } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
    enabled: open,
  });

  const createCategoryMutation = useMutation({
    mutationFn: async (data: InsertCategory) => {
      return await apiRequest("POST", "/api/categories", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      toast({
        title: "Category Created",
        description: "New category has been added successfully.",
      });
      form.reset();
      setShowForm(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create category. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteCategoryMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/categories/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      toast({
        title: "Category Deleted",
        description: "Category has been removed successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to delete category. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertCategory) => {
    createCategoryMutation.mutate(data);
  };

  const handleEdit = (category: any) => {
    setEditingCategory(category);
    form.reset({
      name: category.name,
      color: category.color,
      icon: category.icon,
    });
    setShowForm(true);
  };

  const handleDelete = (id: string) => {
    if (confirm('Are you sure you want to delete this category? This action cannot be undone.')) {
      deleteCategoryMutation.mutate(id);
    }
  };

  const resetForm = () => {
    setEditingCategory(null);
    form.reset();
    setShowForm(false);
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-4xl max-h-[90vh] overflow-y-auto" data-testid="modal-categories-manager">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Tag className="h-5 w-5" />
            <span>Manage Categories</span>
          </DialogTitle>
          <DialogDescription>
            Create and organize expense categories to better track your spending.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Add Category Button */}
          <div className="flex justify-between items-center">
            <h3 className="text-lg font-medium">Your Categories</h3>
            <Button 
              onClick={() => setShowForm(true)}
              data-testid="button-add-category"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Category
            </Button>
          </div>

          {/* Categories Grid */}
          {isLoading ? (
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {[...Array(8)].map((_, i) => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-4">
                    <div className="h-16 bg-gray-200 rounded"></div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4" data-testid="categories-grid">
              {(categories as any)?.map((category: any) => (
                <Card key={category.id} className="relative group hover:shadow-md transition-shadow">
                  <CardContent className="p-4">
                    <div className="flex flex-col items-center space-y-2">
                      <div 
                        className="w-12 h-12 rounded-full flex items-center justify-center text-xl"
                        style={{ backgroundColor: `${category.color}20`, color: category.color }}
                      >
                        {category.icon}
                      </div>
                      <h4 className="font-medium text-center text-sm" data-testid={`category-name-${category.id}`}>
                        {category.name}
                      </h4>
                      <Badge 
                        variant="outline" 
                        className="text-xs"
                        style={{ borderColor: category.color, color: category.color }}
                      >
                        {category.color}
                      </Badge>
                    </div>

                    {/* Action Buttons - Show on Hover */}
                    <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity flex space-x-1">
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-8 w-8 p-0"
                        onClick={() => handleEdit(category)}
                        data-testid={`button-edit-category-${category.id}`}
                      >
                        <Edit className="h-3 w-3" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-8 w-8 p-0"
                        onClick={() => handleDelete(category.id)}
                        data-testid={`button-delete-category-${category.id}`}
                      >
                        <Trash2 className="h-3 w-3 text-red-500" />
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Category Form */}
          {showForm && (
            <Card className="border-2 border-primary/20">
              <CardHeader>
                <CardTitle className="text-lg">
                  {editingCategory ? 'Edit Category' : 'Create New Category'}
                </CardTitle>
              </CardHeader>
              <CardContent>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  {/* Category Name */}
                  <div>
                    <Label htmlFor="name">Category Name *</Label>
                    <Input
                      id="name"
                      placeholder="e.g., Groceries, Entertainment"
                      {...form.register('name')}
                      data-testid="input-category-name"
                    />
                    {form.formState.errors.name && (
                      <p className="text-red-500 text-xs mt-1">{form.formState.errors.name.message}</p>
                    )}
                  </div>

                  {/* Icon Selection */}
                  <div>
                    <Label htmlFor="icon">Icon</Label>
                    <div className="grid grid-cols-8 gap-2 mt-2">
                      {PRESET_ICONS.map((icon) => (
                        <button
                          key={icon}
                          type="button"
                          className={`p-2 text-xl border rounded hover:bg-gray-50 transition-colors ${
                            form.watch('icon') === icon ? 'border-primary bg-primary/10' : 'border-gray-200'
                          }`}
                          onClick={() => form.setValue('icon', icon)}
                          data-testid={`button-icon-${icon}`}
                        >
                          {icon}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Color Selection */}
                  <div>
                    <Label htmlFor="color">Color</Label>
                    <div className="grid grid-cols-8 gap-2 mt-2">
                      {PRESET_COLORS.map((color) => (
                        <button
                          key={color}
                          type="button"
                          className={`w-8 h-8 rounded border-2 transition-all ${
                            form.watch('color') === color ? 'border-gray-800 scale-110' : 'border-gray-300'
                          }`}
                          style={{ backgroundColor: color }}
                          onClick={() => form.setValue('color', color)}
                          data-testid={`button-color-${color}`}
                        />
                      ))}
                    </div>
                    <Input
                      type="color"
                      value={form.watch('color')}
                      onChange={(e) => form.setValue('color', e.target.value)}
                      className="w-20 h-8 mt-2"
                      data-testid="input-custom-color"
                    />
                  </div>

                  {/* Preview */}
                  <div className="p-4 border rounded-lg bg-gray-50">
                    <Label className="text-sm font-medium text-gray-700">Preview</Label>
                    <div className="flex items-center space-x-3 mt-2">
                      <div 
                        className="w-10 h-10 rounded-full flex items-center justify-center text-lg"
                        style={{ 
                          backgroundColor: `${form.watch('color')}20`, 
                          color: form.watch('color') 
                        }}
                      >
                        {form.watch('icon')}
                      </div>
                      <span className="font-medium">{form.watch('name') || 'Category Name'}</span>
                    </div>
                  </div>

                  {/* Form Buttons */}
                  <div className="flex space-x-2 pt-4">
                    <Button 
                      type="submit" 
                      disabled={createCategoryMutation.isPending}
                      data-testid="button-submit-category"
                    >
                      {editingCategory ? 'Update Category' : 'Create Category'}
                    </Button>
                    <Button 
                      type="button" 
                      variant="outline" 
                      onClick={resetForm}
                      data-testid="button-cancel-category"
                    >
                      Cancel
                    </Button>
                  </div>
                </form>
              </CardContent>
            </Card>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/expense-chart.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState, useMemo } from "react";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useToast } from "@/hooks/use-toast";
import {
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer,
  Tooltip,
  Legend,
} from "recharts";

export function ExpenseChart() {
  const { toast } = useToast();
  const [period, setPeriod] = useState("30");

  // Memoize date range calculation to prevent unnecessary API calls
  const dateRange = useMemo(() => {
    const days = parseInt(period);
    const endDate = new Date();
    const startDate = new Date();
    startDate.setDate(startDate.getDate() - days);
    return {
      startDate: startDate.toISOString().split('T')[0],
      endDate: endDate.toISOString().split('T')[0],
    };
  }, [period]);

  const { data: stats, isLoading, error } = useQuery({
    queryKey: ['/api/analytics/stats', dateRange],
    queryFn: async () => {
      const response = await fetch('/api/analytics/stats');
      if (!response.ok) {
        throw new Error('Failed to fetch stats');
      }
      return response.json();
    },
    retry: false,
  });

  const { data: financeNews, isLoading: newsLoading } = useQuery({
    queryKey: ['/api/finance-news'],
    queryFn: async () => {
      const response = await fetch('/api/finance-news');
      if (!response.ok) {
        throw new Error('Failed to fetch finance news');
      }
      return response.json();
    },
    retry: false,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  // Memoize currency formatter - MOVED BEFORE EARLY RETURNS
  const formatCurrency = useMemo(() => {
    const formatter = new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
    });
    return (value: number) => formatter.format(value);
  }, []);

  // Memoize chart data to prevent unnecessary re-renders - MOVED BEFORE EARLY RETURNS
  const categoryData = useMemo(() => {
    const data = (stats as any)?.categoryBreakdown?.map((cat: any) => ({
      name: cat.categoryName,
      value: cat.amount,
      color: cat.color,
    })) || [];

    return data;
  }, [stats]);

  // Color palette for pie chart
  const COLORS = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD', '#98D8C8', '#F7DC6F'];

  if (isLoading || newsLoading) {
    return (
      <div className="grid grid-cols-1 gap-8 lg:grid-cols-2">
        <Card className="animate-pulse">
          <CardHeader>
            <div className="h-6 bg-gray-200 rounded w-1/3"></div>
          </CardHeader>
          <CardContent>
            <div className="h-64 bg-gray-200 rounded"></div>
          </CardContent>
        </Card>
        <Card className="animate-pulse">
          <CardHeader>
            <div className="h-6 bg-gray-200 rounded w-1/3"></div>
          </CardHeader>
          <CardContent>
            <div className="h-64 bg-gray-200 rounded"></div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const handleNewsClick = (newsItem: any) => {
    // Open news article in new tab/window
    if (newsItem.url) {
      window.open(newsItem.url, '_blank');
    }
  };

  return (
    <>
      {/* Finance News Feed */}
      <Card className="bg-white shadow">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-6">
          <CardTitle className="text-lg font-medium text-gray-900">üìà Finance News</CardTitle>
          <span className="text-sm text-gray-500">Latest Updates</span>
        </CardHeader>
        <CardContent>
          <div className="h-64 overflow-y-auto space-y-3" data-testid="finance-news-feed">
            {(financeNews as any) && (financeNews as any).length > 0 ? (
              (financeNews as any).map((newsItem: any, index: number) => (
                <div 
                  key={index}
                  className="p-3 border border-gray-200 rounded-lg hover:bg-gray-50 cursor-pointer transition-colors"
                  onClick={() => handleNewsClick(newsItem)}
                  data-testid={`news-item-${index}`}
                >
                  <h4 className="font-medium text-sm text-gray-900 mb-1 line-clamp-2">
                    {newsItem.title}
                  </h4>
                  <p className="text-xs text-gray-600 mb-2 line-clamp-2">
                    {newsItem.description}
                  </p>
                  <div className="flex justify-between items-center text-xs text-gray-500">
                    <span className="font-medium">{newsItem.source}</span>
                    <span>{newsItem.publishedAt}</span>
                  </div>
                </div>
              ))
            ) : (
              <div className="flex items-center justify-center h-full text-gray-500">
                <div className="text-center">
                  <p className="text-sm font-medium">üì∞ Finance news loading...</p>
                  <p className="text-xs">Stay updated with latest market trends</p>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Enhanced Category Breakdown Chart */}
      <Card className="bg-white shadow">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-6">
          <CardTitle className="text-lg font-medium text-gray-900">üí∞ Spending Breakdown</CardTitle>
          <Select value={period} onValueChange={setPeriod}>
            <SelectTrigger className="w-32" data-testid="select-category-period">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="7">Last 7 days</SelectItem>
              <SelectItem value="30">Last 30 days</SelectItem>
              <SelectItem value="90">Last 90 days</SelectItem>
            </SelectContent>
          </Select>
        </CardHeader>
        <CardContent>
          <div className="h-64" data-testid="chart-category-breakdown">
            {categoryData.length > 0 ? (
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={categoryData}
                    cx="50%"
                    cy="50%"
                    outerRadius={70}
                    innerRadius={30}
                    dataKey="value"
                    label={({ name, percent }) => `${(percent * 100).toFixed(0)}%`}
                    labelLine={false}
                  >
                    {categoryData.map((entry: any, index: number) => (
                      <Cell 
                        key={`category-cell-${entry.name}-${index}`} 
                        fill={entry.color || COLORS[index % COLORS.length]} 
                      />
                    ))}
                  </Pie>
                  <Tooltip 
                    formatter={(value: number) => [formatCurrency(value), 'Amount']}
                    labelFormatter={(label) => `Category: ${label}`}
                  />
                  <Legend 
                    verticalAlign="bottom" 
                    height={36}
                    formatter={(value) => (
                      <span className="text-xs">{value}</span>
                    )}
                  />
                </PieChart>
              </ResponsiveContainer>
            ) : (
              <div className="flex items-center justify-center h-full text-gray-500">
                <div className="text-center">
                  <p className="text-lg font-medium">üìä No expenses yet</p>
                  <p className="text-sm">Add your first expense to see the breakdown</p>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </>
  );
}

client/src/components/expense-form.tsx.content:
import React, { useState, useCallback, useRef } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { insertExpenseSchema, type InsertExpense } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { Loader2, Sparkles } from "lucide-react";

interface ExpenseFormProps {
  open: boolean;
  onClose: () => void;
}

export function ExpenseForm({ open, onClose }: ExpenseFormProps) {
  const { toast } = useToast();
  const [isAICategorizing, setIsAICategorizing] = useState(false);

  const form = useForm<InsertExpense>({
    resolver: zodResolver(insertExpenseSchema),
    defaultValues: {
      amount: "",
      description: "",
      categoryId: "",
      paymentMethod: "cash",
      date: new Date().toISOString().split('T')[0],
    },
  });

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
  });

  const createExpenseMutation = useMutation({
    mutationFn: async (data: InsertExpense) => {
      return await apiRequest("POST", "/api/expenses", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/expenses'] });
      queryClient.invalidateQueries({ queryKey: ['/api/analytics/stats'] });
      queryClient.invalidateQueries({ queryKey: ['/api/insights'] });
      toast({
        title: "Expense Added",
        description: "Your expense has been successfully recorded. AI will analyze your spending patterns.",
      });
      form.reset();
      onClose();

      // Auto-generate insights after adding expense (with slight delay to allow DB update)
      setTimeout(() => {
        fetch('/api/insights/generate', { 
          method: 'POST',
          credentials: 'include'
        }).catch(err => console.log('Auto-insight generation failed:', err));
      }, 1000);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add expense. Please try again.",
        variant: "destructive",
      });
    },
  });

  const categorizeMutation = useMutation({
    mutationFn: async ({ description, amount }: { description: string; amount: number }) => {
      return await apiRequest("POST", "/api/ai/categorize", { description, amount });
    },
    onSuccess: (data: any) => {
      if (data.suggestedCategoryId) {
        form.setValue('categoryId', data.suggestedCategoryId);
        toast({
          title: "AI Suggestion",
          description: `Suggested category: ${data.suggestedCategoryName} (${Math.round(data.confidence * 100)}% confidence)`,
        });
      }
      setIsAICategorizing(false);
    },
    onError: (error: Error) => {
      console.error('AI categorization failed:', error);
      setIsAICategorizing(false);
    },
  });

  const handleAICategorization = async () => {
    const description = form.getValues('description');
    const amount = parseFloat(form.getValues('amount'));

    if (!description.trim()) {
      toast({
        title: "Description Required",
        description: "Please enter a description to use AI categorization.",
        variant: "destructive",
      });
      return;
    }

    if (!amount || amount <= 0) {
      toast({
        title: "Amount Required",
        description: "Please enter a valid amount for better AI categorization.",
        variant: "destructive",
      });
      return;
    }

    setIsAICategorizing(true);
    categorizeMutation.mutate({ description, amount });
  };

  // Auto-trigger AI categorization when both description and amount are present
  const watchDescription = form.watch('description');
  const watchAmount = form.watch('amount');
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Stable function to avoid useEffect dependency issues
  const triggerAutoCategorization = useCallback((description: string, amount: number) => {
    setIsAICategorizing(true);
    categorizeMutation.mutate({ description, amount });
  }, [categorizeMutation]);

  React.useEffect(() => {
    // Clear existing timeout
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    const description = watchDescription?.trim();
    const amount = parseFloat(watchAmount || '0');

    // Auto-categorize if we have good description and amount, but no category selected yet
    if (description && description.length > 3 && amount > 0 && !form.getValues('categoryId') && !isAICategorizing) {
      timeoutRef.current = setTimeout(() => {
        triggerAutoCategorization(description, amount);
      }, 1500); // Delay to avoid too many API calls while typing
    }

    // Cleanup function
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [watchDescription, watchAmount, isAICategorizing, triggerAutoCategorization]);

  const onSubmit = (data: InsertExpense) => {
    createExpenseMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md" data-testid="modal-add-expense">
        <DialogHeader>
          <DialogTitle>Add New Expense</DialogTitle>
        </DialogHeader>

        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          {/* Amount */}
          <div>
            <Label htmlFor="amount">Amount *</Label>
            <div className="mt-1 relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <span className="text-gray-500 text-sm">‚Çπ</span>
              </div>
              <Input
                id="amount"
                type="number"
                step="0.01"
                placeholder="0.00"
                className="pl-7"
                {...form.register('amount')}
                data-testid="input-amount"
              />
            </div>
            {form.formState.errors.amount && (
              <p className="text-red-500 text-xs mt-1">{form.formState.errors.amount.message}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <Label htmlFor="description">Description *</Label>
            <div className="mt-1 flex space-x-2">
              <Input
                id="description"
                placeholder="e.g., Starbucks Coffee"
                {...form.register('description')}
                data-testid="input-description"
              />
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={handleAICategorization}
                disabled={isAICategorizing || categorizeMutation.isPending}
                data-testid="button-ai-categorize"
              >
                {isAICategorizing ? (
                  <Loader2 className="h-4 w-4 animate-spin" />
                ) : (
                  <Sparkles className="h-4 w-4" />
                )}
              </Button>
            </div>
            <p className="text-xs text-gray-500 mt-1">AI will automatically suggest a category</p>
            {form.formState.errors.description && (
              <p className="text-red-500 text-xs mt-1">{form.formState.errors.description.message}</p>
            )}
          </div>

          {/* Category */}
          <div>
            <Label htmlFor="category">Category</Label>
            <Select value={form.watch('categoryId') || ''} onValueChange={(value) => form.setValue('categoryId', value)}>
              <SelectTrigger data-testid="select-category">
                <SelectValue placeholder="Select or let AI suggest..." />
              </SelectTrigger>
              <SelectContent>
                {(categories as any)?.map((category: any) => (
                  <SelectItem key={category.id} value={category.id}>
                    <div className="flex items-center space-x-2">
                      <span className="text-sm">{category.icon}</span>
                      <span>{category.name}</span>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Payment Method */}
          <div>
            <Label htmlFor="paymentMethod">Payment Method *</Label>
            <Select value={form.watch('paymentMethod')} onValueChange={(value) => form.setValue('paymentMethod', value as any)}>
              <SelectTrigger data-testid="select-payment-method">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="cash">Cash</SelectItem>
                <SelectItem value="credit_card">Credit Card</SelectItem>
                <SelectItem value="debit_card">Debit Card</SelectItem>
                <SelectItem value="upi">UPI</SelectItem>
                <SelectItem value="bank_transfer">Bank Transfer</SelectItem>
              </SelectContent>
            </Select>
            {form.formState.errors.paymentMethod && (
              <p className="text-red-500 text-xs mt-1">{form.formState.errors.paymentMethod.message}</p>
            )}
          </div>

          {/* Date */}
          <div>
            <Label htmlFor="date">Date *</Label>
            <Input
              id="date"
              type="date"
              {...form.register('date')}
              data-testid="input-date"
            />
            {form.formState.errors.date && (
              <p className="text-red-500 text-xs mt-1">{form.formState.errors.date.message}</p>
            )}
          </div>

          {/* Buttons */}
          <div className="flex space-x-2 pt-4">
            <Button 
              type="submit" 
              disabled={createExpenseMutation.isPending}
              className="flex-1"
              data-testid="button-submit-expense"
            >
              {createExpenseMutation.isPending ? (
                <Loader2 className="h-4 w-4 animate-spin mr-2" />
              ) : null}
              Add Expense
            </Button>
            <Button 
              type="button" 
              variant="outline" 
              onClick={onClose}
              data-testid="button-cancel-expense"
            >
              Cancel
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

