import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useToast } from "@/hooks/use-toast";
import { formatDistanceToNow } from "date-fns";
import { useMemo } from "react";

// Memoized category helper functions to prevent recreation on every render
const getCategoryIcon = (categoryName: string): string => {
  const name = categoryName?.toLowerCase() || '';
  if (name.includes('food') || name.includes('dining')) return 'üçΩÔ∏è';
  if (name.includes('transport')) return 'üöó';
  if (name.includes('shopping')) return 'üõçÔ∏è';
  if (name.includes('entertainment')) return 'üé¨';
  if (name.includes('bills') || name.includes('utilities')) return 'üì±';
  if (name.includes('healthcare')) return 'üè•';
  if (name.includes('education')) return 'üìö';
  if (name.includes('travel')) return '‚úàÔ∏è';
  return 'üí∞';
};

const getCategoryColor = (categoryName: string): string => {
  const name = categoryName?.toLowerCase() || '';
  if (name.includes('food') || name.includes('dining')) return 'bg-red-100 text-red-800';
  if (name.includes('transport')) return 'bg-blue-100 text-blue-800';
  if (name.includes('shopping')) return 'bg-green-100 text-green-800';
  if (name.includes('entertainment')) return 'bg-amber-100 text-amber-800';
  if (name.includes('bills') || name.includes('utilities')) return 'bg-purple-100 text-purple-800';
  if (name.includes('healthcare')) return 'bg-pink-100 text-pink-800';
  if (name.includes('education')) return 'bg-cyan-100 text-cyan-800';
  if (name.includes('travel')) return 'bg-lime-100 text-lime-800';
  return 'bg-gray-100 text-gray-800';
};

export function RecentExpenses() {
  const { toast } = useToast();

  const { data: expenses, isLoading } = useQuery({
    queryKey: ['/api/expenses', { limit: 10 }],
    retry: false,
  });

  // Memoize formatters to prevent recreation on every render
  const { formatCurrency, formatPaymentMethod } = useMemo(() => {
    const currencyFormatter = new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
    });

    const paymentMethods: Record<string, string> = {
      cash: 'Cash',
      credit_card: 'Credit Card',
      debit_card: 'Debit Card',
      upi: 'UPI',
      bank_transfer: 'Bank Transfer',
    };

    return {
      formatCurrency: (amount: string) => currencyFormatter.format(parseFloat(amount)),
      formatPaymentMethod: (method: string) => paymentMethods[method] || method
    };
  }, []);

  if (isLoading) {
    return (
      <Card className="bg-white shadow animate-pulse">
        <CardHeader>
          <div className="h-6 bg-gray-200 rounded w-1/3"></div>
        </CardHeader>
        <CardContent>
          <div className="divide-y divide-gray-200">
            {[...Array(5)].map((_, i) => (
              <div key={`expense-skeleton-${i}`} className="py-4">
                <div className="h-16 bg-gray-200 rounded" aria-label="Loading expense item"></div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-white shadow">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
        <CardTitle className="text-lg font-medium text-gray-900">Recent Expenses</CardTitle>
        <Button variant="ghost" size="sm" data-testid="button-view-all-expenses">
          View All
        </Button>
      </CardHeader>
      <CardContent className="p-0">
        {(expenses as any) && (expenses as any).length > 0 ? (
          <div className="divide-y divide-gray-200" data-testid="expenses-list" role="list" aria-label="Recent expenses list">
            {(expenses as any).map((expense: any) => (
              <div 
                key={`expense-${expense.id}`} 
                className="px-6 py-4 flex items-center justify-between hover:bg-gray-50 transition-colors"
                data-testid={`expense-item-${expense.id}`}
                role="listitem"
                aria-label={`Expense: ${expense.description}, Amount: ${formatCurrency(expense.amount)}`}
              >
                <div className="flex items-center space-x-3">
                  <div 
                    className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center text-lg"
                    aria-label={`Category: ${expense.category?.name || 'Uncategorized'}`}
                  >
                    {getCategoryIcon(expense.category?.name)}
                  </div>
                  <div>
                    <p className="text-sm font-medium text-gray-900" data-testid={`expense-description-${expense.id}`}>
                      {expense.description}
                    </p>
                    <div className="flex items-center space-x-2 mt-1">
                      <Badge 
                        variant="secondary" 
                        className={getCategoryColor(expense.category?.name)}
                        data-testid={`expense-category-${expense.id}`}
                      >
                        {expense.category?.name || 'Uncategorized'}
                      </Badge>
                      <span className="text-xs text-gray-500" data-testid={`expense-payment-${expense.id}`}>
                        {formatPaymentMethod(expense.paymentMethod)}
                      </span>
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <p className="text-sm font-medium text-gray-900" data-testid={`expense-amount-${expense.id}`}>
                    {formatCurrency(expense.amount)}
                  </p>
                  <p className="text-xs text-gray-500" data-testid={`expense-date-${expense.id}`}>
                    {formatDistanceToNow(new Date(expense.createdAt), { addSuffix: true })}
                  </p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 px-6">
            <div className="w-12 h-12 bg-gray-100 rounded-lg flex items-center justify-center mx-auto mb-4">
              <span className="text-2xl">üí∞</span>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No expenses yet</h3>
            <p className="text-gray-600">
              Start tracking your expenses to see them here.
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/reports-export.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Download, FileText, Calendar, Filter, TrendingUp } from "lucide-react";
import { PDFExport } from "./pdf-export";

interface ReportsExportProps {
  open: boolean;
  onClose: () => void;
}

export function ReportsExport({ open, onClose }: ReportsExportProps) {
  const { toast } = useToast();
  const [reportType, setReportType] = useState("summary");
  const [dateRange, setDateRange] = useState("month");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [showPDFExport, setShowPDFExport] = useState(false);

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
    enabled: open,
  });

  const getDateRange = () => {
    const today = new Date();
    let start = new Date();
    let end = new Date();

    switch (dateRange) {
      case "week":
        start.setDate(today.getDate() - 7);
        break;
      case "month":
        start.setMonth(today.getMonth() - 1);
        break;
      case "quarter":
        start.setMonth(today.getMonth() - 3);
        break;
      case "year":
        start.setFullYear(today.getFullYear() - 1);
        break;
      case "custom":
        return { startDate, endDate };
      default:
        start.setDate(today.getDate() - 30);
    }

    return {
      startDate: start.toISOString().split('T')[0],
      endDate: end.toISOString().split('T')[0]
    };
  };

  const handleExport = async (format: 'csv' | 'pdf') => {
    try {
      if (format === 'pdf') {
        setShowPDFExport(true);
        return;
      }

      const { startDate: start, endDate: end } = getDateRange();

      // Simulate CSV export functionality
      toast({
        title: "Export Started",
        description: `Generating ${format.toUpperCase()} report for ${reportType}...`,
      });

      // Here you would implement the actual CSV export logic
      // For now, we'll simulate a download
      setTimeout(() => {
        const link = document.createElement('a');
        link.href = '#'; // This would be the actual file URL
        link.download = `expense-report-${reportType}-${start}-to-${end}.${format}`;

        toast({
          title: "Export Complete",
          description: `Your ${format.toUpperCase()} report has been generated successfully.`,
        });
      }, 2000);

    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Failed to generate report. Please try again.",
        variant: "destructive",
      });
    }
  };

  const reportTypes = [
    { value: "summary", label: "Expense Summary", description: "Overall spending overview with totals and averages" },
    { value: "detailed", label: "Detailed Transactions", description: "Complete list of all expenses with categories" },
    { value: "category", label: "Category Breakdown", description: "Spending analysis by category with percentages" },
    { value: "trends", label: "Spending Trends", description: "Time-based analysis showing spending patterns" },
    { value: "budget", label: "Budget Analysis", description: "Budget vs actual spending comparison" },
  ];

  return (
    <>
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-2xl" data-testid="modal-reports-export">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <FileText className="h-5 w-5" />
            <span>Export Reports</span>
          </DialogTitle>
          <DialogDescription>
            Generate and download detailed expense reports in various formats.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Report Type Selection */}
          <div>
            <Label htmlFor="reportType">Report Type</Label>
            <div className="grid grid-cols-1 gap-3 mt-2">
              {reportTypes.map((type) => (
                <Card 
                  key={type.value} 
                  className={`cursor-pointer transition-all ${
                    reportType === type.value ? 'border-primary bg-primary/5' : 'hover:border-gray-300'
                  }`}
                  onClick={() => setReportType(type.value)}
                  data-testid={`card-report-type-${type.value}`}
                >
                  <CardContent className="p-4">
                    <div className="flex items-start space-x-3">
                      <div className={`w-4 h-4 rounded-full border-2 mt-0.5 ${
                        reportType === type.value ? 'border-primary bg-primary' : 'border-gray-300'
                      }`} />
                      <div>
                        <h4 className="font-medium">{type.label}</h4>
                        <p className="text-sm text-gray-600 mt-1">{type.description}</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>

          {/* Date Range Selection */}
          <div>
            <Label htmlFor="dateRange">Date Range</Label>
            <Select value={dateRange} onValueChange={setDateRange}>
              <SelectTrigger data-testid="select-date-range">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="week">Last 7 days</SelectItem>
                <SelectItem value="month">Last 30 days</SelectItem>
                <SelectItem value="quarter">Last 3 months</SelectItem>
                <SelectItem value="year">Last year</SelectItem>
                <SelectItem value="custom">Custom range</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Custom Date Range */}
          {dateRange === "custom" && (
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="startDate">Start Date</Label>
                <Input
                  id="startDate"
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  data-testid="input-start-date"
                />
              </div>
              <div>
                <Label htmlFor="endDate">End Date</Label>
                <Input
                  id="endDate"
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  data-testid="input-end-date"
                />
              </div>
            </div>
          )}

          {/* Category Filter */}
          <div>
            <Label htmlFor="categoryFilter">Category Filter</Label>
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger data-testid="select-category-filter">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                {(categories as any)?.map((category: any) => (
                  <SelectItem key={category.id} value={category.id}>
                    <div className="flex items-center space-x-2">
                      <span className="text-sm">{category.icon}</span>
                      <span>{category.name}</span>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Export Buttons */}
          <div className="border-t pt-6">
            <h4 className="font-medium mb-4">Export Format</h4>
            <div className="grid grid-cols-2 gap-4">
              <Button 
                onClick={() => handleExport('csv')}
                className="h-16 flex-col"
                variant="outline"
                data-testid="button-export-csv"
              >
                <Download className="h-5 w-5 mb-2" />
                <div>
                  <div className="font-medium">CSV Format</div>
                  <div className="text-xs text-gray-600">Excel compatible</div>
                </div>
              </Button>

              <Button 
                onClick={() => handleExport('pdf')}
                className="h-16 flex-col"
                variant="outline"
                data-testid="button-export-pdf"
              >
                <FileText className="h-5 w-5 mb-2" />
                <div>
                  <div className="font-medium">PDF Format</div>
                  <div className="text-xs text-gray-600">Formatted report</div>
                </div>
              </Button>
            </div>
          </div>

          {/* Report Preview */}
          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="p-4">
              <div className="flex items-start space-x-3">
                <TrendingUp className="h-5 w-5 text-blue-600 mt-0.5" />
                <div>
                  <h4 className="font-medium text-blue-900">Report Preview</h4>
                  <p className="text-sm text-blue-700 mt-1">
                    This {reportTypes.find(t => t.value === reportType)?.label.toLowerCase()} will include{' '}
                    {dateRange === 'custom' ? `data from ${startDate} to ${endDate}` : `the ${dateRange === 'week' ? 'last 7 days' : dateRange === 'month' ? 'last 30 days' : dateRange === 'quarter' ? 'last 3 months' : 'last year'}`}
                    {categoryFilter !== 'all' && ' for the selected category'}.
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>

    {/* PDF Export Dialog */}
    {showPDFExport && (
      <Dialog open={showPDFExport} onOpenChange={setShowPDFExport}>
        <DialogContent className="sm:max-w-md">
          <PDFExport onClose={() => setShowPDFExport(false)} />
        </DialogContent>
      </Dialog>
    )}
    </>
  );
}

client/src/components/sidebar.tsx.content:
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";
import { useLocation } from "wouter";
import { 
  LayoutDashboard, 
  Plus, 
  Tags, 
  FileText, 
  Brain,
  Target,
  LogOut,
  User
} from "lucide-react";

const navigation = [
  { name: 'Dashboard', href: '/', icon: LayoutDashboard, action: null },
  { name: 'Add Expense', href: '#', icon: Plus, action: 'add-expense' },
  { name: 'Categories', href: '#', icon: Tags, action: 'manage-categories' },
  { name: 'Budgets', href: '#', icon: Target, action: 'budget-settings' },
  { name: 'Reports', href: '/reports', icon: FileText, action: null },
  { name: 'AI Insights', href: '/ai-insights', icon: Brain, action: null },
];

interface SidebarProps {
  onNavigate?: (action: string) => void;
}

export function Sidebar({ onNavigate }: SidebarProps) {
  const { user, logoutMutation } = useAuth();
  const [location, navigate] = useLocation();

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  const handleNavClick = (item: any) => {
    if (item.href && item.href !== '#') {
      navigate(item.href);
    } else if (item.action && onNavigate) {
      onNavigate(item.action);
    }
  };

  return (
    <div className="flex-1 flex flex-col min-h-0 bg-white border-r border-gray-200">
