import React, { useState, useMemo, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Download, FileText, Loader2 } from 'lucide-react';
import { useQuery } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

interface PDFExportProps {
  onClose: () => void;
}

export function PDFExport({ onClose }: PDFExportProps) {
  const [isGenerating, setIsGenerating] = useState(false);
  const { toast } = useToast();

  const { data: expenses = [] } = useQuery({
    queryKey: ['/api/expenses'],
  });

  const { data: analytics } = useQuery({
    queryKey: ['/api/analytics/stats'],
  });

  const { data: insights = [] } = useQuery({
    queryKey: ['/api/insights'],
  });

  const generatePDF = async () => {
    setIsGenerating(true);
    try {
      // Create a temporary container for the report
      const reportElement = document.createElement('div');
      reportElement.style.width = '800px';
      reportElement.style.padding = '20px';
      reportElement.style.backgroundColor = 'white';
      reportElement.style.fontFamily = 'Arial, sans-serif';
      reportElement.style.position = 'absolute';
      reportElement.style.left = '-9999px';

      const currentDate = new Date().toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });

      reportElement.innerHTML = `
        <div style="text-align: center; margin-bottom: 30px;">
          <img src="/@assets/generated_images/SpendSight_finance_tracker_logo_f7c8de5f.png" 
               alt="SpendSight Logo" 
               style="width: 50px; height: 50px; margin-bottom: 10px;">
          <h1 style="color: #2563eb; margin: 10px 0;">SpendSight Financial Report</h1>
          <p style="color: #666; margin: 0;">Generated on ${currentDate}</p>
        </div>

        <div style="margin-bottom: 30px;">
          <h2 style="color: #333; border-bottom: 2px solid #6366f1; padding-bottom: 5px;">Financial Summary</h2>
          <div style="display: grid; grid-template-columns: repeat(2, 1fr); gap: 20px; margin-top: 15px;">
            <div style="background: #f8fafc; padding: 15px; border-radius: 8px;">
              <h3 style="margin: 0 0 5px 0; color: #333;">Total Spent</h3>
              <p style="font-size: 24px; font-weight: bold; color: #ef4444; margin: 0;">‚Çπ${(analytics as any)?.totalSpent || 0}</p>
            </div>
            <div style="background: #f8fafc; padding: 15px; border-radius: 8px;">
              <h3 style="margin: 0 0 5px 0; color: #333;">Total Expenses</h3>
              <p style="font-size: 24px; font-weight: bold; color: #6366f1; margin: 0;">${(expenses as any[]).length}</p>
            </div>
          </div>
        </div>

        <div style="margin-bottom: 30px;">
          <h2 style="color: #333; border-bottom: 2px solid #6366f1; padding-bottom: 5px;">Category Breakdown</h2>
          <div style="margin-top: 15px;">
            ${(analytics as any)?.categoryBreakdown?.map((cat: any) => `
              <div style="display: flex; justify-content: space-between; align-items: center; padding: 10px; border-bottom: 1px solid #e5e7eb;">
                <div style="display: flex; align-items: center;">
                  <div style="width: 12px; height: 12px; background-color: ${cat.color}; border-radius: 50%; margin-right: 10px;"></div>
                  <span style="font-weight: 500;">${cat.categoryName}</span>
                </div>
                <span style="font-weight: bold;">‚Çπ${cat.amount}</span>
              </div>
            `).join('') || '<p>No category data available</p>'}
          </div>
        </div>

        <div style="margin-bottom: 30px;">
          <h2 style="color: #333; border-bottom: 2px solid #6366f1; padding-bottom: 5px;">AI Insights</h2>
          <div style="margin-top: 15px;">
            ${Array.isArray(insights) ? insights.slice(0, 3).map((insight: any, index: number) => 
              `<div style="background: ${insight.type === 'alert' ? '#fef2f2' : insight.type === 'warning' ? '#fffbeb' : insight.type === 'goal' ? '#f0fdf4' : '#f8fafc'}; 
                          border-left: 4px solid ${insight.type === 'alert' ? '#ef4444' : insight.type === 'warning' ? '#f59e0b' : insight.type === 'goal' ? '#10b981' : '#6366f1'}; 
                          padding: 15px; margin-bottom: 15px; border-radius: 0 8px 8px 0;">
                <h3 style="margin: 0 0 8px 0; color: #333; font-size: 16px;">‚Ä¢ ${insight.title}</h3>
                <p style="margin: 0; color: #666; line-height: 1.5;">${insight.description}</p>
              </div>`
            ).join('') : '<p>No insights available</p>'}
          </div>
        </div>

        <div style="margin-bottom: 30px;">
          <h2 style="color: #333; border-bottom: 2px solid #6366f1; padding-bottom: 5px;">Recent Expenses</h2>
          <table style="width: 100%; border-collapse: collapse; margin-top: 15px;">
            <thead>
              <tr style="background: #f8fafc;">
                <th style="padding: 12px; text-align: left; border: 1px solid #e5e7eb;">Date</th>
                <th style="padding: 12px; text-align: left; border: 1px solid #e5e7eb;">Description</th>
                <th style="padding: 12px; text-align: left; border: 1px solid #e5e7eb;">Category</th>
                <th style="padding: 12px; text-align: right; border: 1px solid #e5e7eb;">Amount</th>
              </tr>
            </thead>
            <tbody>
              ${Array.isArray(expenses) ? expenses.slice(0, 10).map((expense: any) => 
                `<tr>
                  <td style="padding: 10px; border: 1px solid #e5e7eb;">${new Date(expense.date).toLocaleDateString()}</td>
                  <td style="padding: 10px; border: 1px solid #e5e7eb;">${expense.description}</td>
                  <td style="padding: 10px; border: 1px solid #e5e7eb;">${expense.category?.name || 'Uncategorized'}</td>
                  <td style="padding: 10px; border: 1px solid #e5e7eb; text-align: right;">‚Çπ${expense.amount}</td>
                </tr>`
              ).join('') : '<tr><td colspan="4" style="padding: 20px; text-align: center;">No expenses found</td></tr>'}
            </tbody>
          </table>
        </div>

        <div style="text-align: center; margin-top: 40px; padding-top: 20px; border-top: 1px solid #e5e7eb;">
          <p style="color: #666; font-size: 12px; margin: 0;">Generated by SpendSight - Smart Financial Management</p>
        </div>
      `;

      document.body.appendChild(reportElement);

      // Generate canvas from the element
      const canvas = await html2canvas(reportElement, {
        scale: 2,
        useCORS: true,
        allowTaint: false,
        backgroundColor: '#ffffff'
      });

      // Remove temporary element
      document.body.removeChild(reportElement);

      // Create PDF
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgWidth = 210;
      const pageHeight = 295;
      const imgHeight = (canvas.height * imgWidth) / canvas.width;
      let heightLeft = imgHeight;
      let position = 0;

      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }

      // Download the PDF
      const fileName = `SpendSight_Report_${new Date().toISOString().split('T')[0]}.pdf`;
      pdf.save(fileName);

      toast({
        title: "Export Successful",
        description: "Your financial report has been downloaded as PDF.",
      });

      onClose();
    } catch (error) {
      console.error('PDF generation failed:', error);
      toast({
        title: "Export Failed",
        description: "Failed to generate PDF report. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <FileText className="h-5 w-5 text-primary" />
          <span>Export PDF Report</span>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="text-sm text-gray-600">
          <p>Generate a comprehensive financial report including:</p>
          <ul className="list-disc list-inside mt-2 space-y-1">
            <li>Financial summary and statistics</li>
            <li>Category breakdown with visualizations</li>
            <li>AI-generated insights and recommendations</li>
            <li>Recent expense transactions</li>
          </ul>
        </div>

        <div className="flex space-x-2">
          <Button 
            onClick={generatePDF} 
            disabled={isGenerating}
            className="flex-1"
            data-testid="button-generate-pdf"
          >
            {isGenerating ? (
              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
            ) : (
              <Download className="h-4 w-4 mr-2" />
            )}
            {isGenerating ? 'Generating...' : 'Download PDF'}
          </Button>
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/hooks/useWebSocket.ts.content:
import { useEffect, useRef, useState } from 'react';
import { useToast } from '@/hooks/use-toast';
import { queryClient } from '@/lib/queryClient';

interface WebSocketMessage {
  type: string;
  data?: any;
  timestamp?: number;
}

export function useWebSocket() {
  const [isConnected, setIsConnected] = useState(false);
  const [lastMessage, setLastMessage] = useState<WebSocketMessage | null>(null);
  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const { toast } = useToast();

  const connect = () => {
    try {
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/ws`;

      const ws = new WebSocket(wsUrl);
      wsRef.current = ws;

      ws.onopen = () => {
        console.log('üîó WebSocket connected');
        setIsConnected(true);

        // Clear any existing reconnect timeout
        if (reconnectTimeoutRef.current) {
          clearTimeout(reconnectTimeoutRef.current);
          reconnectTimeoutRef.current = null;
        }
      };

      ws.onmessage = (event) => {
        try {
          const message: WebSocketMessage = JSON.parse(event.data);
          setLastMessage(message);

          // Handle different message types
          switch (message.type) {
            case 'connection':
              console.log('‚úÖ WebSocket connection confirmed');
              break;

            case 'expense_update':
              console.log('üí∞ Expense update received:', message.data);
              // Invalidate relevant queries to refresh data
              queryClient.invalidateQueries({ queryKey: ['/api/expenses'] });
              queryClient.invalidateQueries({ queryKey: ['/api/analytics/stats'] });

              toast({
                title: "Expense Updated",
                description: "Your expenses have been updated in real-time.",
              });
              break;

            case 'insights_update':
              console.log('üß† Insights update received:', message.data);
              queryClient.invalidateQueries({ queryKey: ['/api/insights'] });

              toast({
                title: "New Insights Available",
                description: "Fresh AI insights have been generated for your expenses.",
              });
              break;

            case 'analytics_update':
              console.log('üìä Analytics update received:', message.data);
              queryClient.invalidateQueries({ queryKey: ['/api/analytics/stats'] });
              break;

            case 'pong':
              console.log('üèì WebSocket ping-pong successful');
              break;

            default:
              console.log('üì® Unknown message type:', message.type);
          }
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };

      ws.onclose = () => {
        console.log('üîå WebSocket disconnected');
        setIsConnected(false);

        // Attempt to reconnect after 3 seconds
        reconnectTimeoutRef.current = setTimeout(() => {
          console.log('üîÑ Attempting to reconnect WebSocket...');
          connect();
        }, 3000);
      };

      ws.onerror = (error) => {
        console.error('WebSocket error:', error);
        setIsConnected(false);
      };

    } catch (error) {
      console.error('Failed to create WebSocket connection:', error);
      setIsConnected(false);
    }
  };

  const disconnect = () => {
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }

    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }

    setIsConnected(false);
  };

  const sendMessage = (message: WebSocketMessage) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(message));
    } else {
      console.warn('WebSocket not connected, cannot send message');
    }
  };

  const sendPing = () => {
    sendMessage({ type: 'ping', timestamp: Date.now() });
  };

  useEffect(() => {
    connect();

    // Cleanup on unmount
    return () => {
      disconnect();
    };
  }, []);

  // Ping every 30 seconds to keep connection alive
  useEffect(() => {
    if (isConnected) {
      const pingInterval = setInterval(sendPing, 30000);
      return () => clearInterval(pingInterval);
    }
  }, [isConnected]);

  return {
    isConnected,
    lastMessage,
    sendMessage,
    sendPing,
    connect,
    disconnect
  };
}

client/src/components/budget-progress.ts.content:
import React, { useMemo } from 'react';
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { TrendingUp, TrendingDown, Target, DollarSign, AlertTriangle, CheckCircle } from "lucide-react";

interface BudgetProgressProps {
  onOpenBudgetSettings?: () => void;
}

export function BudgetProgress({ onOpenBudgetSettings }: BudgetProgressProps) {
  const { data: budgets, isLoading } = useQuery({
    queryKey: ['/api/budgets'],
    retry: false,
  });

  const { data: expenses } = useQuery({
    queryKey: ['/api/expenses'],
    retry: false,
  });

  const { data: userProfile } = useQuery({
    queryKey: ['/api/user/profile'],
    retry: false,
  });

  const budgetData = useMemo(() => {
    if (!budgets || !expenses) return [];

    return (budgets as any[]).map((budget: any) => {
      const currentMonth = new Date().getMonth();
      const currentYear = new Date().getFullYear();

      // Calculate spent amount for this budget's category in current period
      const categoryExpenses = (expenses as any[]).filter((expense: any) => {
        const expenseDate = new Date(expense.date);
        const isCurrentPeriod = budget.period === 'monthly' 
          ? expenseDate.getMonth() === currentMonth && expenseDate.getFullYear() === currentYear
          : true; // Add logic for weekly/yearly if needed

        return isCurrentPeriod && (
          !budget.categoryId || 
          budget.categoryId === 'all' || 
          expense.categoryId === budget.categoryId
        );
      });

      const spent = categoryExpenses.reduce((sum: number, expense: any) => sum + parseFloat(expense.amount), 0);
      const budgetAmount = parseFloat(budget.amount);
      const percentage = Math.min((spent / budgetAmount) * 100, 100);
      const remaining = Math.max(budgetAmount - spent, 0);

      return {
        ...budget,
        spent,
        budgetAmount,
        percentage,
        remaining,
        isOverBudget: spent > budgetAmount,
        categoryExpenses: categoryExpenses.length
      };
    });
  }, [budgets, expenses]);

  const monthlyIncome = useMemo(() => {
    return (userProfile as any)?.monthlyIncome || 0;
  }, [userProfile]);

  const totalBudgetedAmount = useMemo(() => {
    return budgetData.reduce((sum, budget) => sum + budget.budgetAmount, 0);
  }, [budgetData]);

  const totalSpentAmount = useMemo(() => {
    return budgetData.reduce((sum, budget) => sum + budget.spent, 0);
  }, [budgetData]);

  const incomeUtilization = useMemo(() => {
    if (!monthlyIncome) return 0;
    return Math.min((totalBudgetedAmount / monthlyIncome) * 100, 100);
  }, [monthlyIncome, totalBudgetedAmount]);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const getProgressColor = (percentage: number, isOverBudget: boolean) => {
    if (isOverBudget) return "bg-red-500";
    if (percentage >= 80) return "bg-yellow-500";
    if (percentage >= 60) return "bg-orange-500";
    return "bg-green-500";
  };

  if (isLoading) {
    return (
      <Card className="bg-white shadow animate-pulse">
        <CardHeader>
          <div className="h-6 bg-gray-200 rounded w-1/2"></div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-20 bg-gray-200 rounded"></div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-white shadow" data-testid="budget-progress-card">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
        <CardTitle className="flex items-center space-x-2 text-lg font-medium text-gray-900">
          <Target className="h-5 w-5 text-blue-500" />
          <span>Budget Progress</span>
        </CardTitle>
        {onOpenBudgetSettings && (
          <Button 
            variant="outline" 
            size="sm"
            onClick={onOpenBudgetSettings}
            data-testid="button-manage-budgets"
          >
            Manage
          </Button>
        )}
      </CardHeader>
      <CardContent>
        {/* Income vs Budget Overview */}
        {monthlyIncome > 0 && (
          <div className="mb-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
            <div className="flex justify-between items-center mb-2">
              <span className="text-sm font-medium text-blue-900">Monthly Income Allocation</span>
              <span className="text-sm text-blue-700">{incomeUtilization.toFixed(1)}%</span>
            </div>
            <Progress 
              value={incomeUtilization} 
              className="h-2 mb-2" 
            />
            <div className="flex justify-between text-xs text-blue-600">
              <span>Income: {formatCurrency(monthlyIncome)}</span>
              <span>Budgeted: {formatCurrency(totalBudgetedAmount)}</span>
            </div>
          </div>
        )}

        {budgetData && budgetData.length > 0 ? (
          <div className="space-y-4" data-testid="budget-progress-list">
            {budgetData.map((budget: any, index: number) => (
              <div 
                key={budget.id}
                className="p-4 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
                data-testid={`budget-progress-${index}`}
              >
                <div className="flex justify-between items-start mb-3">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-1">
                      <h4 className="font-medium text-sm text-gray-900">
                        {budget.categoryName || 'All Categories'}
                      </h4>
                      <Badge 
                        variant={budget.isOverBudget ? "destructive" : budget.percentage >= 80 ? "secondary" : "default"}
                        className="text-xs"
                      >
                        {budget.isOverBudget ? (
                          <>
                            <AlertTriangle className="h-3 w-3 mr-1" />
                            Over Budget
                          </>
                        ) : budget.percentage >= 80 ? (
                          <>
                            <TrendingUp className="h-3 w-3 mr-1" />
                            Near Limit
                          </>
                        ) : (
                          <>
                            <CheckCircle className="h-3 w-3 mr-1" />
                            On Track
                          </>
                        )}
                      </Badge>
                    </div>
                    <div className="flex items-center text-xs text-gray-500 space-x-3">
                      <span>{budget.period} ‚Ä¢ {budget.categoryExpenses} transactions</span>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm font-medium text-gray-900">
                      {formatCurrency(budget.spent)} / {formatCurrency(budget.budgetAmount)}
                    </div>
                    <div className={`text-xs ${budget.isOverBudget ? 'text-red-600' : 'text-green-600'}`}>
                      {budget.isOverBudget ? 'Exceeded by ' : 'Remaining: '}
                      {formatCurrency(budget.isOverBudget ? budget.spent - budget.budgetAmount : budget.remaining)}
                    </div>
                  </div>
                </div>

                <div className="space-y-2">
                  <div className="flex justify-between text-xs text-gray-600">
                    <span>Progress</span>
                    <span>{budget.percentage.toFixed(1)}%</span>
                  </div>
                  <Progress 
                    value={budget.percentage} 
                    className="h-2" 
                    // className={`h-2 ${getProgressColor(budget.percentage, budget.isOverBudget)}`}
                  />
                </div>
              </div>
            ))}

            {/* Total Summary */}
            <div className="mt-6 p-4 bg-gray-50 rounded-lg border border-gray-200">
              <div className="flex justify-between items-center">
                <span className="font-medium text-gray-900">Total this month</span>
                <div className="text-right">
                  <div className="font-medium text-gray-900">
                    {formatCurrency(totalSpentAmount)} / {formatCurrency(totalBudgetedAmount)}
                  </div>
                  <div className="text-xs text-gray-600">
                    {((totalSpentAmount / totalBudgetedAmount) * 100).toFixed(1)}% of budget used
                  </div>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div className="text-center py-8">
            <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No budgets set</h3>
            <p className="text-gray-600 mb-4">
              Create budgets to track your spending and reach your financial goals.
            </p>
            {onOpenBudgetSettings && (
              <Button onClick={onOpenBudgetSettings}>
                <Target className="h-4 w-4 mr-2" />
                Set Your First Budget
              </Button>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/finance-news.tsx.content:
import React, { useState, useMemo, useCallback } from 'react';
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { ExternalLink, TrendingUp, DollarSign, Building, Globe, RefreshCw } from "lucide-react";

interface NewsItem {
  title: string;
  description: string;
  url: string;
  source: string;
  publishedAt: string;
  category: string;
}

interface FinanceNewsProps {
  compact?: boolean;
}

export function FinanceNews({ compact = false }: FinanceNewsProps) {
  const { toast } = useToast();
  const [selectedCategory, setSelectedCategory] = useState<string>('all');

  const { data: financeNews, isLoading, refetch } = useQuery({
    queryKey: ['/api/finance-news'],
    queryFn: async () => {
      const response = await fetch('/api/finance-news');
      if (!response.ok) {
        throw new Error('Failed to fetch finance news');
      }
      return response.json();
    },
    retry: false,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  const handleNewsClick = useCallback((newsItem: NewsItem) => {
    if (newsItem.url) {
      window.open(newsItem.url, '_blank', 'noopener,noreferrer');
    }
  }, []);

  const handleRefresh = useCallback(() => {
    refetch();
    toast({
      title: "Refreshing News",
      description: "Getting latest finance updates...",
    });
  }, [refetch, toast]);

  const categories = useMemo(() => {
    if (!financeNews) return [];
    const cats = ['all', ...new Set((financeNews as NewsItem[]).map(item => item.category))];
    return cats;
  }, [financeNews]);

  const filteredNews = useMemo(() => {
    if (!financeNews) return [];
    if (selectedCategory === 'all') return financeNews as NewsItem[];
    return (financeNews as NewsItem[]).filter(item => item.category === selectedCategory);
  }, [financeNews, selectedCategory]);

  const getCategoryIcon = (category: string) => {
    switch (category.toLowerCase()) {
      case 'fintech': return <TrendingUp className="h-3 w-3" />;
      case 'banking': return <Building className="h-3 w-3" />;
      case 'markets': return <DollarSign className="h-3 w-3" />;
      default: return <Globe className="h-3 w-3" />;
    }
  };

  if (isLoading) {
    return (
      <Card className={`bg-white shadow ${compact ? 'h-64' : 'h-96'}`}>
        <CardHeader className="pb-3">
          <div className="h-6 bg-gray-200 rounded w-1/3 animate-pulse"></div>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {[...Array(compact ? 3 : 5)].map((_, i) => (
              <div key={i} className="animate-pulse">
                <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                <div className="h-3 bg-gray-200 rounded w-1/2"></div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`bg-white shadow ${compact ? 'h-64' : 'h-96'}`} data-testid="finance-news-card">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-3">
        <CardTitle className="flex items-center space-x-2 text-lg font-medium text-gray-900">
          <TrendingUp className="h-5 w-5 text-orange-500" />
          <span>üìà Finance News</span>
        </CardTitle>
        <Button 
          variant="ghost" 
          size="sm" 
          onClick={handleRefresh}
          disabled={isLoading}
          data-testid="button-refresh-news"
        >
          <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
        </Button>
      </CardHeader>
      <CardContent>
        {!compact && categories.length > 1 && (
          <div className="flex flex-wrap gap-2 mb-4">
            {categories.map((category) => (
              <Badge
                key={category}
                variant={selectedCategory === category ? "default" : "outline"}
                className="cursor-pointer text-xs"
                onClick={() => setSelectedCategory(category)}
              >
                {getCategoryIcon(category)}
                <span className="ml-1 capitalize">{category}</span>
              </Badge>
            ))}
          </div>
        )}

        <div 
          className={`${compact ? 'h-44' : 'h-64'} overflow-y-auto space-y-3`} 
          data-testid="finance-news-feed"
        >
          {filteredNews && filteredNews.length > 0 ? (
            filteredNews.slice(0, compact ? 4 : 8).map((newsItem: NewsItem, index: number) => (
              <div 
                key={index}
                className="p-3 border border-gray-200 rounded-lg hover:bg-orange-50 hover:border-orange-300 cursor-pointer transition-all duration-200 group"
                onClick={() => handleNewsClick(newsItem)}
                data-testid={`news-item-${index}`}
              >
                <div className="flex justify-between items-start mb-2">
                  <h4 className="font-medium text-sm text-gray-900 group-hover:text-orange-700 line-clamp-2 flex-1">
                    {newsItem.title}
                  </h4>
                  <ExternalLink className="h-3 w-3 text-gray-400 group-hover:text-orange-500 ml-2 flex-shrink-0" />
                </div>

                {!compact && (
                  <p className="text-xs text-gray-600 mb-2 line-clamp-2">
                    {newsItem.description}
                  </p>
                )}

                <div className="flex justify-between items-center text-xs">
                  <div className="flex items-center space-x-2">
                    <Badge variant="secondary" className="text-xs">
                      {getCategoryIcon(newsItem.category)}
                      <span className="ml-1">{newsItem.source}</span>
                    </Badge>
                  </div>
                  <span className="text-gray-500">
                    {new Date(newsItem.publishedAt).toLocaleDateString('en-IN')}
                  </span>
                </div>
              </div>
            ))
          ) : (
            <div className="flex items-center justify-center h-full text-gray-500">
              <div className="text-center">
                <TrendingUp className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                <p className="text-sm font-medium">üì∞ Loading finance news...</p>
                <p className="text-xs">Stay updated with latest market trends</p>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/ai-insights.tsx.content:
import React, { useState, useMemo, useCallback } from 'react';
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { 
  Brain, 
  TrendingUp, 
  TrendingDown, 
  AlertTriangle, 
  Target, 
  PiggyBank, 
  Calendar,
  DollarSign,
  BarChart3,
  Lightbulb,
  RefreshCw,
  Award,
  CheckCircle
} from "lucide-react";

interface InsightData {
  type: 'spending_pattern' | 'budget_alert' | 'savings_opportunity' | 'goal_tracking';
  title: string;
  description: string;
  severity: 'low' | 'medium' | 'high';
  category?: string;
  amount?: number;
  percentage?: number;
  recommendation: string;
  actionable: boolean;
}

interface SpendingAnalysis {
  totalSpent: number;
  avgDailySpend: number;
  topCategory: string;
  topCategoryAmount: number;
  monthOverMonth: number;
  unusualTransactions: number;
  predictedMonthlySpend: number;
}

export default function AIInsightsPage() {
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState("overview");

  const { data: insights, isLoading: insightsLoading, refetch: refetchInsights } = useQuery({
    queryKey: ['/api/insights'],
    retry: false,
  });

  const { data: expenses } = useQuery({
    queryKey: ['/api/expenses'],
    retry: false,
  });

  const { data: budgets } = useQuery({
    queryKey: ['/api/budgets'],
    retry: false,
  });

  const generateInsightsMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/ai/generate-insights");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/insights'] });
      toast({
        title: "AI Analysis Complete",
        description: "Fresh insights generated based on your latest spending patterns.",
      });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized", 
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to generate insights. Please try again.",
        variant: "destructive",
      });
    },
  });

  const spendingAnalysis = useMemo((): SpendingAnalysis => {
    if (!expenses) return {
      totalSpent: 0,
      avgDailySpend: 0,
      topCategory: '',
      topCategoryAmount: 0,
      monthOverMonth: 0,
      unusualTransactions: 0,
      predictedMonthlySpend: 0
    };

    const expenseData = expenses as any[];
    const currentMonth = new Date().getMonth();
    const currentYear = new Date().getFullYear();

    const thisMonthExpenses = expenseData.filter(expense => {
      const expenseDate = new Date(expense.date);
      return expenseDate.getMonth() === currentMonth && expenseDate.getFullYear() === currentYear;
    });

    const totalSpent = thisMonthExpenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0);
    const daysInMonth = new Date().getDate();
    const avgDailySpend = totalSpent / daysInMonth;

    // Category analysis
    const categoryTotals = thisMonthExpenses.reduce((acc, expense) => {
      const category = expense.categoryName || 'Other';
      acc[category] = (acc[category] || 0) + parseFloat(expense.amount);
      return acc;
    }, {} as Record<string, number>);

    const topCategory = Object.entries(categoryTotals).reduce((max, [cat, amount]) => 
      amount > max[1] ? [cat, amount] : max, ['', 0]);

    // Predict monthly spend
    const daysRemaining = new Date(currentYear, currentMonth + 1, 0).getDate() - daysInMonth;
    const predictedMonthlySpend = totalSpent + (avgDailySpend * daysRemaining);

    return {
      totalSpent,
      avgDailySpend,
      topCategory: topCategory[0],
      topCategoryAmount: topCategory[1],
      monthOverMonth: 0, // Would need previous month data
      unusualTransactions: 0, // Would need more complex analysis
      predictedMonthlySpend
    };
  }, [expenses]);

  const handleGenerateInsights = useCallback(() => {
    generateInsightsMutation.mutate();
  }, [generateInsightsMutation]);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'high': return 'text-red-600 bg-red-100';
      case 'medium': return 'text-yellow-600 bg-yellow-100';
      default: return 'text-green-600 bg-green-100';
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'high': return <AlertTriangle className="h-4 w-4" />;
      case 'medium': return <TrendingUp className="h-4 w-4" />;
      default: return <CheckCircle className="h-4 w-4" />;
    }
  };

  if (insightsLoading) {
    return (
      <div className="min-h-screen bg-background p-6">
        <div className="max-w-7xl mx-auto">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-gray-200 rounded w-1/3"></div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {[...Array(6)].map((_, i) => (
                <div key={i} className="h-32 bg-gray-200 rounded"></div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 flex items-center space-x-3">
              <Brain className="h-8 w-8 text-purple-500" />
              <span>AI Financial Insights</span>
            </h1>
            <p className="text-gray-600 mt-2">
              Intelligent analysis of your spending patterns and personalized financial recommendations
            </p>
          </div>
          <Button 
            onClick={handleGenerateInsights}
            disabled={generateInsightsMutation.isPending}
            className="bg-purple-600 hover:bg-purple-700"
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${generateInsightsMutation.isPending ? 'animate-spin' : ''}`} />
            {generateInsightsMutation.isPending ? 'Analyzing...' : 'Refresh Insights'}
          </Button>
        </div>

        {/* Quick Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card className="bg-gradient-to-r from-blue-500 to-blue-600 text-white">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-blue-100 text-sm">This Month</p>
                  <p className="text-2xl font-bold">{formatCurrency(spendingAnalysis.totalSpent)}</p>
                </div>
                <DollarSign className="h-8 w-8 text-blue-200" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-r from-green-500 to-green-600 text-white">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-green-100 text-sm">Daily Average</p>
                  <p className="text-2xl font-bold">{formatCurrency(spendingAnalysis.avgDailySpend)}</p>
                </div>
                <BarChart3 className="h-8 w-8 text-green-200" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-r from-purple-500 to-purple-600 text-white">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-purple-100 text-sm">Top Category</p>
                  <p className="text-lg font-bold truncate">{spendingAnalysis.topCategory || 'None'}</p>
                  <p className="text-sm text-purple-200">{formatCurrency(spendingAnalysis.topCategoryAmount)}</p>
                </div>
                <Target className="h-8 w-8 text-purple-200" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-r from-orange-500 to-orange-600 text-white">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-orange-100 text-sm">Predicted Monthly</p>
                  <p className="text-2xl font-bold">{formatCurrency(spendingAnalysis.predictedMonthlySpend)}</p>
                </div>
                <TrendingUp className="h-8 w-8 text-orange-200" />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Main Content Tabs */}
        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="overview">Overview</TabsTrigger>
            <TabsTrigger value="patterns">Spending Patterns</TabsTrigger>
            <TabsTrigger value="recommendations">Recommendations</TabsTrigger>
            <TabsTrigger value="goals">Goals</TabsTrigger>
          </TabsList>

          <TabsContent value="overview" className="space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* AI Insights */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Lightbulb className="h-5 w-5 text-yellow-500" />
                    <span>Smart Insights</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {(insights as any) && (insights as any).length > 0 ? (
                    <div className="space-y-4">
                      {(insights as any).slice(0, 3).map((insight: InsightData, index: number) => (
                        <div key={index} className={`p-4 rounded-lg border ${getSeverityColor(insight.severity)}`}>
                          <div className="flex items-start space-x-3">
                            {getSeverityIcon(insight.severity)}
                            <div className="flex-1">
                              <h4 className="font-medium text-sm">{insight.title}</h4>
                              <p className="text-xs mt-1 opacity-90">{insight.description}</p>
                              {insight.actionable && (
                                <Badge variant="outline" className="mt-2 text-xs">
                                  <Target className="h-3 w-3 mr-1" />
                                  Actionable
                                </Badge>
                              )}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8">
                      <Brain className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                      <p className="text-gray-600">No insights generated yet</p>
                      <Button 
                        onClick={handleGenerateInsights}
                        className="mt-4"
                        disabled={generateInsightsMutation.isPending}
                      >
                        Generate Insights
                      </Button>
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Budget Health */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <PiggyBank className="h-5 w-5 text-green-500" />
                    <span>Budget Health</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {(budgets as any) && (budgets as any).length > 0 ? (
                    <div className="space-y-4">
                      {(budgets as any).slice(0, 3).map((budget: any, index: number) => {
                        const spent = 0; // Would calculate from expenses
                        const budgetAmount = parseFloat(budget.amount) || 1;
                        const percentage = (spent / budgetAmount) * 100;
                        return (
                          <div key={index} className="space-y-2">
                            <div className="flex justify-between text-sm">
                              <span className="font-medium">{budget.categoryName || 'All Categories'}</span>
                              <span>{percentage.toFixed(0)}%</span>
                            </div>
                            <Progress value={percentage} className="h-2" />
                            <div className="flex justify-between text-xs text-gray-600">
                              <span>{formatCurrency(spent)}</span>
                              <span>{formatCurrency(parseFloat(budget.amount))}</span>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  ) : (
                    <div className="text-center py-8">
                      <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                      <p className="text-gray-600">No budgets set</p>
                      <p className="text-xs text-gray-500">Create budgets to track your progress</p>
                    </div>
                  )}
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          <TabsContent value="patterns" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Spending Pattern Analysis</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8">
                  <BarChart3 className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">Advanced pattern analysis coming soon</p>
                  <p className="text-xs text-gray-500">We're building detailed spending behavior insights</p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="recommendations" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Personalized Recommendations</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8">
                  <Award className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">AI recommendations will appear here</p>
                  <p className="text-xs text-gray-500">Based on your spending patterns and goals</p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="goals" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Financial Goals Tracking</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8">
                  <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <p className="text-gray-600">Goal tracking feature coming soon</p>
                  <p className="text-xs text-gray-500">Set and monitor your financial objectives</p>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/reports.tsx.content:
import React, { useState, useMemo, useCallback } from 'react';
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { 
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line,
  Legend
} from "recharts";
import { 
  Download, 
  FileText, 
  Calendar, 
  Filter,
  TrendingUp,
  DollarSign,
  PieChart as PieChartIcon,
  BarChart3,
  Printer,
  Mail,
  RefreshCw
} from "lucide-react";
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

interface ReportFilters {
  startDate: string;
  endDate: string;
  category: string;
  minAmount: string;
  maxAmount: string;
}

export default function ReportsPage() {
  const { toast } = useToast();
  const [filters, setFilters] = useState<ReportFilters>({
    startDate: new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0],
    endDate: new Date().toISOString().split('T')[0],
    category: 'all',
    minAmount: '',
    maxAmount: ''
  });
  const [reportType, setReportType] = useState('summary');
  const [isExporting, setIsExporting] = useState(false);

  const { data: expenses, isLoading: expensesLoading } = useQuery({
    queryKey: ['/api/expenses'],
    retry: false,
  });

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
    retry: false,
  });

  const { data: budgets } = useQuery({
    queryKey: ['/api/budgets'],
    retry: false,
  });

  const filteredExpenses = useMemo(() => {
    if (!expenses) return [];

    return (expenses as any[]).filter(expense => {
      const expenseDate = new Date(expense.date);
      const startDate = new Date(filters.startDate);
      const endDate = new Date(filters.endDate);
      const amount = parseFloat(expense.amount);

      // Date filter
      if (expenseDate < startDate || expenseDate > endDate) return false;

      // Category filter
      if (filters.category !== 'all' && expense.categoryId !== filters.category) return false;

      // Amount filters
      if (filters.minAmount && amount < parseFloat(filters.minAmount)) return false;
      if (filters.maxAmount && amount > parseFloat(filters.maxAmount)) return false;

      return true;
    });
  }, [expenses, filters]);

  const reportData = useMemo(() => {
    const totalAmount = filteredExpenses.reduce((sum, expense) => sum + parseFloat(expense.amount), 0);
    const avgAmount = filteredExpenses.length > 0 ? totalAmount / filteredExpenses.length : 0;

    // Category breakdown
    const categoryBreakdown = filteredExpenses.reduce((acc, expense) => {
      const category = expense.categoryName || 'Other';
      acc[category] = (acc[category] || 0) + parseFloat(expense.amount);
      return acc;
    }, {} as Record<string, number>);

    const categoryData = Object.entries(categoryBreakdown).map(([name, value]) => ({
      name,
      value,
      percentage: ((value / totalAmount) * 100).toFixed(1)
    }));

    // Daily spending trend
    const dailySpending = filteredExpenses.reduce((acc, expense) => {
      const date = new Date(expense.date).toISOString().split('T')[0];
      acc[date] = (acc[date] || 0) + parseFloat(expense.amount);
      return acc;
    }, {} as Record<string, number>);

    const trendData = Object.entries(dailySpending)
      .sort(([a], [b]) => a.localeCompare(b))
      .map(([date, amount]) => ({
        date,
        amount,
        formattedDate: new Date(date).toLocaleDateString('en-IN', { month: 'short', day: 'numeric' })
      }));

    return {
      totalAmount,
      avgAmount,
      transactionCount: filteredExpenses.length,
      categoryData,
      trendData,
      topCategory: categoryData.length > 0 ? categoryData.reduce((max, cat) => cat.value > max.value ? cat : max) : null
    };
  }, [filteredExpenses]);

  const handleFilterChange = useCallback((key: keyof ReportFilters, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  }, []);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  const exportToPDF = useCallback(async () => {
    setIsExporting(true);
    try {
      const element = document.getElementById('report-content');
      if (!element) return;

      const canvas = await html2canvas(element, {
        scale: 2,
        allowTaint: true,
        useCORS: true
      });

      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgWidth = 210;
      const imgHeight = (canvas.height * imgWidth) / canvas.width;

      pdf.addImage(imgData, 'PNG', 0, 0, imgWidth, imgHeight);
      pdf.save(`SpendSight-Report-${new Date().toISOString().split('T')[0]}.pdf`);

      toast({
        title: "Report Exported",
        description: "Your financial report has been downloaded as PDF.",
      });
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Could not export report. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  }, [toast]);

  const exportToCSV = useCallback(() => {
    const csvData = filteredExpenses.map(expense => ({
      Date: new Date(expense.date).toLocaleDateString('en-IN'),
      Description: expense.description,
      Category: expense.categoryName || 'Other',
      Amount: parseFloat(expense.amount),
    }));

    const headers = Object.keys(csvData[0] || {});
    const csvContent = [
      headers.join(','),
      ...csvData.map(row => headers.map(header => row[header as keyof typeof row]).join(','))
    ].join('\\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `SpendSight-Data-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);

    toast({
      title: "Data Exported",
      description: "Your expense data has been downloaded as CSV.",
    });
  }, [filteredExpenses, toast]);

  const COLORS = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD', '#98D8C8', '#F7DC6F'];

  if (expensesLoading) {
    return (
      <div className="min-h-screen bg-background p-4 md:p-6">
        <div className="max-w-7xl mx-auto">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-gray-200 rounded w-1/3"></div>
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {[...Array(6)].map((_, i) => (
                <div key={i} className="h-48 bg-gray-200 rounded"></div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-4 md:p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex flex-col md:flex-row md:justify-between md:items-center space-y-4 md:space-y-0">
          <div>
            <h1 className="text-2xl md:text-3xl font-bold text-gray-900 flex items-center space-x-3">
              <FileText className="h-6 md:h-8 w-6 md:w-8 text-blue-500" />
              <span>Financial Reports</span>
            </h1>
            <p className="text-gray-600 mt-2 text-sm md:text-base">
              Comprehensive analysis of your spending patterns and financial health
            </p>
          </div>
          <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-2">
            <Button 
              onClick={exportToCSV}
              variant="outline"
              disabled={isExporting || filteredExpenses.length === 0}
              className="w-full sm:w-auto"
            >
              <Download className="h-4 w-4 mr-2" />
              Export CSV
            </Button>
            <Button 
              onClick={exportToPDF}
              disabled={isExporting || filteredExpenses.length === 0}
              className="w-full sm:w-auto"
            >
              {isExporting ? (
                <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
              ) : (
                <Printer className="h-4 w-4 mr-2" />
              )}
              {isExporting ? 'Exporting...' : 'Export PDF'}
            </Button>
          </div>
        </div>

        {/* Filters */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2 text-lg">
              <Filter className="h-5 w-5" />
              <span>Report Filters</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
              <div>
                <Label htmlFor="startDate" className="text-sm">Start Date</Label>
                <Input
                  id="startDate"
                  type="date"
                  value={filters.startDate}
                  onChange={(e) => handleFilterChange('startDate', e.target.value)}
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="endDate" className="text-sm">End Date</Label>
                <Input
                  id="endDate"
                  type="date"
                  value={filters.endDate}
                  onChange={(e) => handleFilterChange('endDate', e.target.value)}
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="category" className="text-sm">Category</Label>
                <Select value={filters.category} onValueChange={(value) => handleFilterChange('category', value)}>
                  <SelectTrigger className="mt-1">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Categories</SelectItem>
                    {(categories as any)?.map((category: any) => (
                      <SelectItem key={category.id} value={category.id}>
                        {category.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="minAmount" className="text-sm">Min Amount (‚Çπ)</Label>
                <Input
                  id="minAmount"
                  type="number"
                  placeholder="0"
                  value={filters.minAmount}
                  onChange={(e) => handleFilterChange('minAmount', e.target.value)}
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="maxAmount" className="text-sm">Max Amount (‚Çπ)</Label>
                <Input
                  id="maxAmount"
                  type="number"
                  placeholder="No limit"
                  value={filters.maxAmount}
                  onChange={(e) => handleFilterChange('maxAmount', e.target.value)}
                  className="mt-1"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Report Content */}
        <div id="report-content" className="space-y-6">
          {/* Summary Cards */}
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 md:gap-6">
            <Card className="bg-gradient-to-r from-blue-500 to-blue-600 text-white">
              <CardContent className="p-4 md:p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-blue-100 text-xs md:text-sm">Total Spent</p>
                    <p className="text-lg md:text-2xl font-bold">{formatCurrency(reportData.totalAmount)}</p>
                  </div>
                  <DollarSign className="h-6 md:h-8 w-6 md:w-8 text-blue-200" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-gradient-to-r from-green-500 to-green-600 text-white">
              <CardContent className="p-4 md:p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-green-100 text-xs md:text-sm">Average</p>
                    <p className="text-lg md:text-2xl font-bold">{formatCurrency(reportData.avgAmount)}</p>
                  </div>
                  <TrendingUp className="h-6 md:h-8 w-6 md:w-8 text-green-200" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-gradient-to-r from-purple-500 to-purple-600 text-white">
              <CardContent className="p-4 md:p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-purple-100 text-xs md:text-sm">Transactions</p>
                    <p className="text-lg md:text-2xl font-bold">{reportData.transactionCount}</p>
                  </div>
                  <BarChart3 className="h-6 md:h-8 w-6 md:w-8 text-purple-200" />
                </div>
              </CardContent>
            </Card>

            <Card className="bg-gradient-to-r from-orange-500 to-orange-600 text-white">
              <CardContent className="p-4 md:p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-orange-100 text-xs md:text-sm">Top Category</p>
                    <p className="text-sm md:text-lg font-bold truncate">
                      {reportData.topCategory?.name || 'None'}
                    </p>
                    {reportData.topCategory && (
                      <p className="text-xs text-orange-200">
                        {formatCurrency(reportData.topCategory.value)}
                      </p>
                    )}
                  </div>
                  <PieChartIcon className="h-6 md:h-8 w-6 md:w-8 text-orange-200" />
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Charts */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Spending Trend */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Spending Trend</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-64 md:h-80">
                  {reportData.trendData.length > 0 ? (
                    <ResponsiveContainer width="100%" height="100%">
                      <LineChart data={reportData.trendData}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                        <XAxis 
                          dataKey="formattedDate" 
                          stroke="#666"
                          fontSize={12}
                        />
                        <YAxis 
                          tickFormatter={(value) => `‚Çπ${value}`}
                          stroke="#666"
                          fontSize={12}
                        />
                        <Tooltip 
                          formatter={(value: number) => [formatCurrency(value), 'Amount']}
                          labelFormatter={(label) => `Date: ${label}`}
                        />
                        <Line 
                          type="monotone" 
                          dataKey="amount" 
                          stroke="#2563eb" 
                          strokeWidth={2}
                          dot={{ fill: '#2563eb', strokeWidth: 2, r: 4 }}
                          activeDot={{ r: 6 }}
                        />
                      </LineChart>
                    </ResponsiveContainer>
                  ) : (
                    <div className="flex items-center justify-center h-full text-gray-500">
                      <div className="text-center">
                        <BarChart3 className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                        <p className="text-sm">No data for selected period</p>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Category Breakdown */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Category Breakdown</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="h-64 md:h-80">
                  {reportData.categoryData.length > 0 ? (
                    <ResponsiveContainer width="100%" height="100%">
                      <PieChart>
                        <Pie
                          data={reportData.categoryData}
                          cx="50%"
                          cy="50%"
                          outerRadius={60}
                          innerRadius={30}
                          dataKey="value"
                          label={({ name, percentage }) => `${name}: ${percentage}%`}
                          labelLine={false}
                          fontSize={12}
                        >
                          {reportData.categoryData.map((entry: any, index: number) => (
                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                          ))}
                        </Pie>
                        <Tooltip formatter={(value: number) => formatCurrency(value)} />
                        <Legend fontSize={12} />
                      </PieChart>
                    </ResponsiveContainer>
                  ) : (
                    <div className="flex items-center justify-center h-full text-gray-500">
                      <div className="text-center">
                        <PieChartIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                        <p className="text-sm">No expenses in selected period</p>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Transaction Details */}
          {filteredExpenses.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Transaction Details</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="overflow-x-auto">
                  <table className="w-full text-sm">
                    <thead>
                      <tr className="border-b">
                        <th className="text-left p-2">Date</th>
                        <th className="text-left p-2">Description</th>
                        <th className="text-left p-2">Category</th>
                        <th className="text-right p-2">Amount</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredExpenses.slice(0, 20).map((expense: any, index: number) => (
                        <tr key={index} className="border-b hover:bg-gray-50">
                          <td className="p-2">{new Date(expense.date).toLocaleDateString('en-IN')}</td>
                          <td className="p-2 truncate max-w-xs">{expense.description}</td>
                          <td className="p-2">
                            <Badge variant="outline" className="text-xs">
                              {expense.categoryName || 'Other'}
                            </Badge>
                          </td>
                          <td className="p-2 text-right font-medium">
                            {formatCurrency(parseFloat(expense.amount))}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                  {filteredExpenses.length > 20 && (
                    <p className="text-center text-gray-500 text-sm mt-4">
                      Showing 20 of {filteredExpenses.length} transactions. Export for full data.
                    </p>
                  )}
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}
